#!/Users/ljl/anaconda3/envs/tensorflow36/bin pythonfrom selenium import webdriverfrom selenium.webdriver.chrome.service import Servicefrom selenium.webdriver.support.wait import WebDriverWaitfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.support import expected_conditions as ECimport timeopt = webdriver.ChromeOptions()opt.headless = Falsedriver = webdriver.Chrome(options=opt)driver.get('http://piao.qunar.com/ticket/list.htm?keyword=%E7%83%AD%E9%97%A8%E6%99%AF%E7%82%B9&sort=pp&page=1')def getData():    sightList = driver.find_elements_by_css_selector('.sight_item')    for sight in sightList:        sightHeader = sight.find_element_by_css_selector('.sight_item_detail.clrfix')        title = sightHeader.find_element_by_css_selector('.sight_item_about').find_element_by_css_selector('.sight_item_caption>a').text        sale = sightHeader.find_element_by_css_selector('.sight_item_pop>table>tbody>tr>td>.hot_num').text        price = sightHeader.find_element_by_css_selector('.sight_item_pop>table>tbody>tr>td>.sight_item_price>em').text        hot = sightHeader.find_element_by_css_selector('.sight_item_about>.sight_item_info>.clrfix>.sight_item_hot>.product_star_level>em').find_element_by_tag_name('span').get_attribute('style')        if str(hot).index('width: ') !=-1 :            hot = str(hot).replace('width: ','')        if str(hot).index('%;') != -1:            hot = str(hot).replace('%;', '')        print(title,price,sale,hot)WebDriverWait(driver, 10).until(EC.visibility_of(driver.find_element(by=By.CLASS_NAME, value='sight_item')))getData()driver.quit()